mapper.select:
select postidx, postname, postcontent, postnick, memidx, uploadfile, 
	   date_format(postdate, '%Y-%m-%d') as postdate, postview, postlike, boardcategory
	   from board where postidx = #{postidx}

mapper.update:
update board 
set postname = #{postname}, postcontent = #{postcontent}, boardcategory = #{boardcategory} 
where postidx = #{postidx};
